import{_ as h,o as t,c as i,k as n}from"./chunks/framework.3Y0MQAQP.js";const _=JSON.parse('{"title":"\\"Chúng ta là tội nhân và cần sự tha thứ\\"","description":"","frontmatter":{"title":"\\"Chúng ta là tội nhân và cần sự tha thứ\\"","date":"2023-09-30T17:00:00.000Z","tags":["thiêng liêng"],"prev":{"text":"Ai rồi cũng khác, là khờ hoặc ác","link":"/baiviet/20230901001-ai-roi-cung-khac-la-kho-hoac-ac.md"},"next":{"text":"Phục vụ từ những điều giản đơn","link":"/baiviet/20231015001-phuc-vu-tu-nhung-dieu-gian-don"}},"headers":[],"relativePath":"baiviet/20231001001-chung-ta-la-toi-nhan-va-can-su-tha-thu.md","filePath":"baiviet/20231001001-chung-ta-la-toi-nhan-va-can-su-tha-thu.md"}'),a={name:"baiviet/20231001001-chung-ta-la-toi-nhan-va-can-su-tha-thu.md"},c=n("p",null,"Đó là câu nói của Đức Thánh Cha Phan-xi-cô trong buổi tiếp kiến chung vào ngày 10/04/ 2019. Khi nhận ra mình có tội, chúng ta thường rơi vào những thái độ: Sợ hãi, hối hận, tìm cách né tránh hay trốn chạy… Thái độ sẽ quyết định hành động của chúng ta và quyết định cả đời sống của chúng ta nữa! Phạm tội thì rất dễ, nhưng quyết định và hành động đúng thì rất khó!",-1),g=n("p",null,"Nhìn lại hình ảnh của ông thu thuế Gia-kêu (Lc, 19, 1-10), khi lại gần với Đức Giêsu, lòng thương xót của Ngài đã chạm đến nơi sâu thẳm của trái tim và ông nhận ra mình là một kẻ tội lỗi cần đến lòng thương xót của Chúa. Điều này đã khiến ông ăn năn hoán cải, xin đền bù lại tất cả thiệt hại mà ông đã gây ra. Hay hình ảnh của người phụ nữ tội lỗi (Lc 7,36-50) khi đối diện với Đức Giêsu, bà đã nhận được sự tha thứ. Sự tha thứ đó đã phá vỡ những lề luật dập khuôn. Nhờ đó đã chạm tới nơi sâu thẳm trong bà và mở ra cho bà một khởi đầu mới.",-1),u=n("p",null,"Khi đối diện với Thiên Chúa, điều quan trọng nhất là chúng ta biết nhận ra mình là một tội nhân yếu đuối, cần đến lòng thương xót của Ngài. Mỗi khi chúng ta chỉ thấy những việc tốt mà mình đã làm, mà không nhận ra những điều mình đã phạm và rồi chỉ biết lên án, những người khác vì tội lỗi của họ, thì chúng ta cũng giống như Pha-ri-siêu vậy (Ga 8,1-11), chẳng thể nào đụng chạm đến được lòng thương xót của Thiên Chúa",-1),l=n("p",null,[n("em",null,"Lạy Đấng Chí Thánh giàu lòng thương xót, xin cho chúng con luôn khiêm tốn biết nhận ra sự bất toàn nơi thân phận mỏng giòn của mình, cảm nghiệm được lòng Chúa xót thương và chạy đến với Ngài. Xin cho chúng con biết noi gương Ngài, xót thương và quảng đại tha thứ trước những yếu đuối, lỗi lầm của anh em, để chúng con thêm hiểu nhau, cùng nhau sống yêu thương và để cùng thực thi điều Chúa dạy.")],-1),o=[c,g,u,l];function e(s,m,r,v,d,p){return t(),i("div",null,o)}const y=h(a,[["render",e]]);export{_ as __pageData,y as default};
