import{D as h,o as t,c,I as g,m as a,k as n}from"./chunks/framework.3Y0MQAQP.js";const e="/assets/mienbac1.4eXzgT51.jpeg",o=n("p",null,"Chân phước Miguel Augustin Pro sinh năm 1891 tại thị trấn Zacatecas, miền trung nước Mêhicô, trong một gia đình khá giả và đạo đức. Ngài được biết đến là chàng trai dí dỏm, với tính cách có phần quậy phá từ hồi còn nhỏ, ngài đã đem lại nhiều niềm vui cho anh em trong cộng đoàn. Cũng phong cách ấy, ngài đã góp phần để phục vụ Chúa khi khéo léo cải trang để tránh khỏi sự truy bắt nhiều lần của chính quyền đàn áp Công giáo thời bấy giờ. Và những gì đối với Chúa, ngài đều có một sự nghiêm túc và cẩn trọng, ngài lén dâng lễ cho giáo dân, thậm chí khi bị cầm tù ngài cũng cử hành một số bí tích cho các tù nhân giam cùng. Ngài tử đạo ngày 23/11/1927 khi bị xử bắn mà không cần bản án. Đồng thời với niềm tin cách mạnh mẽ, Ngài hô vang khẩu khí lớn trước lúc chết “Vạn tuế Vua Ki-tô.”",-1),m=n("p",null,"Lễ bổn mạng Cộng đoàn ứng sinh miền Bắc đã diễn ra cách trọng thể. Vào ngày 23/11/2023 anh em đã được trải qua giờ tĩnh tâm sốt sắng và sau đó tham dự Thánh lễ với sự chủ tế của Cha Giám đốc. Thánh lễ được diễn ra tại nhà nguyện đơn sơ và đầy sốt mến trong Cộng đoàn Vân Canh. Chúa Nhật ngày 26/11/2023 cũng là ngày Lễ Chúa Ki-tô Vua, buổi sáng anh em được học cùng với Cha Giám đốc và tới trưa là bữa tiệc mừng trong giây phút vui tươi gần gũi. Tới buổi chiều anh em được giao lưu với nhau qua môn thể thao bóng đá trong đó có sự hiện diện đầy trân quý của Cha Giám đốc Nhà Ứng Sinh miền Bắc Giu-se Tuân Vũ Chí Thành SJ, Cha Vinh Sơn Vũ Tứ Quyết SJ. Sau cuộc giao lưu là phần trao giải cho hai đội và cầu thủ xuất sắc nhất. Qua ngày lễ mừng mỗi anh em ứng sinh đều nhận được nhiều ơn lành và hoa trái thiêng liêng. Hơn hết đó là tâm tình tạ ơn Thiên Chúa và sự biết ơn quý cha, quý thầy trong Ban huấn luyện, cùng quý ân nhân đã hết long giúp đỡ cho Nhà ứng sinh bằng nhiều phương thế. Xin quý vị tiếp tục cầu nguyện và nâng đỡ để mỗi anh em ứng sinh có thêm động lực tiến bước mỗi ngày một hơn trên hành trình ơn gọi đời tu của mình.",-1),b=JSON.parse('{"title":"Chân phước Miquel Augustin Pro (23/11) - Bổn mạng cộng đoàn ứng sinh miền Bắc","description":"","frontmatter":{"title":"Chân phước Miquel Augustin Pro (23/11) - Bổn mạng cộng đoàn ứng sinh miền Bắc","date":"2023-11-26T17:00:00.000Z","tags":["nhà ứng sinh","cộng đoàn"],"preview_image":"mienbac1.jpg","prev":{"text":"Tri Thức Nâng Cánh Ước Mơ","link":"/baiviet/20231118001-tri-thuc-nang-canh-uoc-mo"},"next":{"text":"Một Thiên Chúa đang sống","link":"/baiviet/20240112001-mot-thien-chua-dang-song"}},"headers":[],"relativePath":"baiviet/20231127001-bon-mang-cong-doan-mien-bac.md","filePath":"baiviet/20231127001-bon-mang-cong-doan-mien-bac.md"}'),u={name:"baiviet/20231127001-bon-mang-cong-doan-mien-bac.md"},v=Object.assign(u,{setup(s){return(r,l)=>{const i=h("Figure");return t(),c("div",null,[g(i,{src:a(e),caption:"Cộng đoàn ứng sinh miền Bắc."},null,8,["src"]),o,m])}}});export{b as __pageData,v as default};
