import{D as c,o as g,c as a,I as i,m as t,k as n}from"./chunks/framework.LLdJMamj.js";const e="/assets/z5072631119765_536aeb5bc9e17938183b2bfe786c23b2.SadNTgWP.webp",m="/assets/v9.vzXsUNbJ.webp",s="/assets/v1.x2ZTMmCo.webp",o="/assets/v4.F2DIh7KF.webp",u="/assets/v5-1.WYVcJT6t.webp",l="/assets/v8.__8ZM1z5.webp",r=n("p",null,"Tôi đã nhìn thấy, nghe thấy và cảm thấy nơi đó có một thông điệp niềm vui của sự sống.",-1),v=n("p",null,"Tôi nhìn thấy một con đường nhiều đất đỏ dẫn vào ngôi làng ấy, nó trải dài qua nhiều khu đồi cao su. Mở ra trước mắt, tôi thấy một giáo xứ nghèo, tương đối thô sơ tuổi đời chỉ vỏn vẹn 2 năm. Đếm ấy, tôi thấy một cô người S’tieng đón chúng tôi về nhà cô ấy. Hai anh em chúng tôi vừa lạ nhau, cũng vừa lạ lạ chỗ mới này. Buổi sáng sớm, tôi chú mắt để thấy những hạt lúa trên một cánh đồng vùng cao, chúng cao to hơn lúa miền đồng bằng, nằm ngã sập sệ, đó là lúa mùa. Mỗi năm chỉ gieo một vụ, thu hoạch sau 6 tháng. Họ gieo xuống, Ông Trời tưới nước, Ông Trời chăm sóc, Ông Trời cũng cho kết quả, họ và chúng tôi ra gặt lúa về. Những hạt lúa căng mọng, đầy sữa chẳng thua gì hạt lúa miền xuôi. 20 bao lúa, đó là thành quả Ông Trời cho họ, cho bao nhiêu họ cũng cảm tạ hết lòng. Đủ nuôi sống trong 1 năm, tới mùa kế tiếp. 1 bao vần gửi lại nhà xứ, tri ân Ông Trời. Sự nổ lực của con người là có hạn nhưng được thần lực nuôi dưỡng và chở che. Tôi thấy nụ cười tươi rói của họ, “chúng tôi không cần tiền, tiền không ăn được; nghèo nhưng vui đầy;” “Ở đây, có gì ăn đấy.” Tôi cảm thấy sự chân thành từ họ, từ việc ăn uống, ngủ nghỉ đến đời sống đức tin của chúng tôi. “Nói một thôi, đừng nói hai lời.” Đối với cô chủ nhà, Chúa là người mẹ của cô ấy, cô ấy là trẻ mồ côi, bây giờ cô ấy nghĩ đến Chúa mỗi khi nhớ đến mẹ mình.",-1),y=n("p",null,"Dưới tán rừng cao su, trước một đống lửa nhỏ, tôi thấy con người chìm vào trong màn đêm với việc nhiệm vụ của mình, thành thục và luôn hy vọng. Người anh em của tôi thật sự tốt đấy, anh ấy kể cho tôi nghe vài câu chuyện của anh ấy, tôi đã nghe và hiểu người bạn này hơn. Chúng tôi cùng nhau làm hang đá, mỗi ông có một sáng kiến, nhưng lại hội nhất trong nhau, 15 cái hang đá, chúng tôi có rất nhiều việc cần làm. Người dân tại mỗi sóc ấy nô nức, hào hứng giúp chúng tôi, họ kể cho chúng tôi những khoảng thời gian đã qua. Mỗi tối, chúng tôi cùng cha xứ tham dự các giờ kinh tại các sóc, đó như một sự kết nối lạ kỳ, hội tụ quanh bàn thờ Chúa trong kia có một tà thần khác trong nhà “cây nêu,” tôi có sợ không, vừa sợ lại vừa không, vì còn có một “cái cây” to hơn nữa trong bầu không khí ngột ngạt u ám ấy. Tôi chìm đắm mình trước sự hiện diện của Thánh Thể mỗi cuối tuần, như một cách thức lắng nghe và hồi tưởng lại những hình ảnh đã qua. “Sao thầy buồn vậy ?” Tôi vừa lắng nghe một cuộc điện thoại, tôi nhìn vào trong tâm tư, tình cảnh của người đó, tôi đã lo lắng.",-1),b=n("p",null,"Có đôi chút ảnh hưởng, nhưng vì sự kiện này khiến tôi đi sâu vào mầu nhiệm “Nhập Thể” : vì yêu mà có tất cả. “Yêu không sợ khổ, không sợ lỗ; Yêu là khiến người mình yêu tốt hơn.” Hình ảnh một vị cha xứ chu đáo, nhiệt thành đến kỳ lạ, vì yêu. Tôi có những người bạn mới trên vùng đất đó: đó là những đứa trẻ tại nhà xứ, đứa bé cạnh nhà, những cô bé múa giỏi, đôi khi có chút phiền, nhưng quy chung chúng thật vui tươi trẻ chung. Tôi còn một công việc đặc biệt: có nhiều bệnh nhân đến với tôi, tôi có chút khả năng cho việc y tế này. Lắng nghe những “đứa con” của tôi, nghe “chúng” kể lại bệnh tình của mình.",-1),p=n("p",null,"Tôi thấy một Đêm Giao Thừa trên mảnh đất biên giới ấy, yên bình và lặng lẽ, với anh em của tôi. Chúng tôi chia tay trong vội vã, để không lưu luyến điều gì, cô chủ nhà và những người bên cạnh, họ đến chào chúng tôi. Chúng tôi có dịp hội họp với nhau. Ban đầu tôi chẳng biết ai, nhưng giờ có 4 gia đình đến chào chúng tôi, họ là người cưu mang chúng tôi, họ là người tôi hay nghe kể chuyện, họ là bệnh nhân của tôi, và có những đứa là học trò của tôi. Tôi đã có những người bạn tại đó.",-1),k=n("p",null,"Tại Đồng Tâm, có một hành trình khác chúng tôi đã tham gia. Cùng đi vào thế giới tự nhiên, lắng nghe tiếng nói của Chúa trong đó. Sự hiện hữu của mọi vật chính là cách thức tôn vinh Thiên Chúa cao đẹp nhất, nhưng thật tốt khi chúng là sinh hoa trái như Chúa muốn.",-1),T=n("p",null,"THÁNG TÔNG ĐỒ – GIÁO XỨ LỘC HÒA – GP. PHÚ CƯỜNG",-1),_=n("p",null,[n("em",null,"Peter.Công Hậu")],-1),S=JSON.parse('{"title":"Nghe, Nhìn Và Cảm Thấy Niềm Vui Của Sự Sống","description":"","frontmatter":{"title":"Nghe, Nhìn Và Cảm Thấy Niềm Vui Của Sự Sống","date":"2024-01-14T17:00:00.000Z","tags":["tông đồ"],"preview_image":"z5072631119765_536aeb5bc9e17938183b2bfe786c23b2.webp","prev":{"text":"Một Thiên Chúa đang sống","link":"/baiviet/20240112001-mot-thien-chua-dang-song"},"next":{"text":"Ngày tri ân quý ân nhân của anh em ứng sinh miền Bắc","link":"/baiviet/20240115002-ngay-tri-an-quy-an-nhan-cua-anh-em-ung-sinh-mien-bac"}},"headers":[],"relativePath":"baiviet/20240115001-nghe-nhin-va-cam-thay-niem-vui-cua-su-song.md","filePath":"baiviet/20240115001-nghe-nhin-va-cam-thay-niem-vui-cua-su-song.md"}'),d={name:"baiviet/20240115001-nghe-nhin-va-cam-thay-niem-vui-cua-su-song.md"},f=Object.assign(d,{setup(C){return(N,x)=>{const h=c("Figure");return g(),a("div",null,[r,i(h,{src:t(e),caption:"Trên con đường sứ vụ."},null,8,["src"]),v,i(h,{src:t(m),caption:"Thu hoạch lúa mùa cùng người dân địa phương trong sự quan phòng của Thiên Chúa."},null,8,["src"]),y,i(h,{src:t(s),caption:"Ánh lửa giữa rẫy."},null,8,["src"]),i(h,{src:t(o),caption:"Đọc kinh bên Hang Đá."},null,8,["src"]),b,p,i(h,{src:t(u),caption:"Đón chào năm mới 2024 cùng nhau trong tình yêu Thiên Chúa."},null,8,["src"]),k,i(h,{src:t(l),caption:"Thánh lễ trên Địa Cầu của anh em Dòng Tên tại Lộc Ninh."},null,8,["src"]),T,_])}}});export{S as __pageData,f as default};
