import{D as i,o as t,c,I as g,m as a,k as n}from"./chunks/framework.LLdJMamj.js";const u="/assets/taon1.NY6I0CFt.jpeg",m=n("p",null,"Năm Phụng vụ bắt đầu với Mùa Vọng, mùa chuẩn bị mừng đại lễ Chúa Giáng sinh. Đây cũng là mùa cho mỗi người được nhìn lại hành trình sống trong suốt một năm. Dẫu có vui buồn, thành công hay thất bại thì chúng ta cũng đều dâng lời cảm tạ với biết bao hồng ân mà Chúa đã dành cho ta. Trong tâm tình ấy, cộng đoàn Ứng sinh Dòng Tên miền Bắc hân hoan mừng ngày tri ân quý ân nhân tại giáo xứ Hàng Bột.",-1),e=n("p",null,"Thật hạnh phúc khi Thánh lễ tri ân đã diễn ra cách trang nghiêm và sốt sắng. Mở đầu Thánh lễ cha Giám đốc Giu-se Tuân Vũ Chí Thành đã kêu gọi cộng đoàn dân Chúa hãy luôn tỉnh thức và sẵn sàng để đón chờ Chúa đến và cầu nguyện cách đặc biệt cho quý ân nhân còn sống cũng như đã qua đời của cộng đoàn Ứng sinh miền Bắc. Trong bài giảng cha chia sẻ về Chúa Giê-su chữa bệnh cho người bại liệt. Một Thiên Chúa đầy yêu thương và lòng thương xót. Ngài luôn kêu mời mỗi người đến với Ngài để được Ngài tha thứ và chữa lành…dẫu rằng chúng ta là những tội nhân nhưng chúng ta được liên kết với Ngài qua mối liên đới lớn mạnh trong Hội Thánh.",-1),s=n("p",null,"Cuối Thánh lễ cha cũng đã giới thiệu đôi nét tình hình của Nhà Ứng sinh miền Bắc hiện nay. Bên cạnh những niềm vui đời tu, thì cũng có nhiều nỗi khó khăn trắc trở mà mỗi anh em đều phải trải qua. Và cũng rất cần đến lời cầu nguyện của quý ân nhân và sự giúp đỡ về nhiều mặt cũng như vật chất để mỗi anh em có điều kiện thăng tiến hơn trong đời sống.",-1),o=n("p",null,"Mỗi anh em ứng sinh ngày nay cũng trải qua cảm giác đôi lúc yếu đuối bất lực khi bản thân có khả năng tự trang trải cho bản thân nhưng vẫn cần đón nhận sự giúp đỡ của những người khác. Đó chính là điều kiện cần trong đời tu, ta vừa cho đi nhưng cũng cần biết đón nhận sự giúp đỡ của người khác, tập sống sự lệ thuộc để trân quý những món quà và biết ơn những ai tặng nó cho ta.",-1),l=n("p",null,"Sau khi Thánh lễ anh em Ứng sinh đã được ngồi lại để nói lên sự tri ân của mình và lắng nghe những câu chuyện của mọi người qua bữa tiệc ngọt. Bên cạnh đó Cha giám đốc cũng đã cảm ơn quý vị ân nhân và cho mọi người xem video về một ngày sống của anh em Ứng sinh để mọi người hiểu hơn về đời sống trong cộng đoàn được diễn ra như nào. Cuối cùng là những tiết mục cây nhà lá vườn do anh em Ứng sinh chuẩn bị và lời cám ơn của một anh em đại diện bày tỏ niềm vui, sự biết ơn và những lời chúc về một mùa Giáng sinh và năm mới an lành.",-1),r=n("p",null,"Đời sống ứng sinh còn nhiều khó khăn, thách đố, cần lắm những sự quan tâm của mỗi người và không thể thiếu những lời cầu nguyện liên lỉ của quý vị. Ước gì trải qua một năm, ta nhìn nhận lại trong mọi hoàn cảnh đều có sự hiện diện của Thiên Chúa quan phòng, để có thốt lên niềm cảm tạ biết ơn mà thăng tiến bản thân hơn trong Năm Mới.",-1),_=JSON.parse('{"title":"Ngày tri ân quý ân nhân của anh em ứng sinh miền Bắc","description":"","frontmatter":{"title":"Ngày tri ân quý ân nhân của anh em ứng sinh miền Bắc","date":"2024-01-14T18:00:00.000Z","tags":["nhà ứng sinh"],"preview_image":"taon1.jpg","prev":{"text":"Nghe, Nhìn Và Cảm Thấy Niềm Vui Của Sự Sống","link":"/baiviet/20240115001-nghe-nhin-va-cam-thay-niem-vui-cua-su-song"},"next":{"text":"Chân phước An-rê Phú Yên (18/01) - Bổn mạng cộng đoàn An-rê","link":"/baiviet/20240119001-bon-mang-cong-doan-an-re-phu-yen"}},"headers":[],"relativePath":"baiviet/20240115002-ngay-tri-an-quy-an-nhan-cua-anh-em-ung-sinh-mien-bac.md","filePath":"baiviet/20240115002-ngay-tri-an-quy-an-nhan-cua-anh-em-ung-sinh-mien-bac.md"}'),v={name:"baiviet/20240115002-ngay-tri-an-quy-an-nhan-cua-anh-em-ung-sinh-mien-bac.md"},k=Object.assign(v,{setup(b){return(p,d)=>{const h=i("Figure");return t(),c("div",null,[g(h,{src:a(u),caption:"Cha Giu-se-Tuân Vũ Chí Thành."},null,8,["src"]),m,e,s,o,l,r])}}});export{_ as __pageData,k as default};
