import{D as c,o as t,c as g,I as i,m as a,k as n}from"./chunks/framework.LLdJMamj.js";const e="/assets/anre1.n-j0wkDG.jpeg",o=n("p",null,[n("em",null,"“Hỡi anh em, đối với Chúa Giêsu yêu dấu của chúng ta, chúng ta hãy lấy tình yêu đáp lại tình yêu. Chúa đã chịu chết đau khổ vì chúng ta, nên chúng ta hãy lấy mạng sống đáp đền mạng sống … ”")],-1),s=n("p",null,[n("em",null,"“Hỡi anh chị em, ta hãy trung tín cùng Đức Chúa Trời cho đến chết; cho đến chết, không một điều gì có thể dập tắt lòng thương mến Chúa Giêsu Kitô trong trái tim ta.”")],-1),u=n("p",null,"Đây là những lời sau cùng của Thầy Giảng Anrê Phú Yên, người đã hy sinh máu đào để bảo vệ đức tin vào ngày 26/7/1644.",-1),m=n("p",null,"Vậy tại sao lịch phụng vụ Dòng Tên lại kính nhớ Chân phước vào ngày 18/1?",-1),l=n("p",null,"Ngày 18/1 - một ngày đặc biệt ý nghĩa đối với tất cả anh em Giêsu Hữu Việt Nam, ngày này đánh dấu sự kiện các Linh mục Dòng Tên khởi hành sứ vụ rao giảng Tin Mừng trên đất “Con rồng cháu tiên”. Vùng đất này sáng lên hình ảnh một thanh niên đạo đức, thánh thiện là Chân phước Anrê Phú Yên - người đã gia nhập vào hội thầy giảng của Cha Đắc Lộ. Nhưng trong bối cảnh lịch sử xã hội lúc bấy giờ còn nhiều biến động, Anrê Phú Yên vì bảo vệ đức tin chân chính của mình đã bị quan Nghè Bộ cho xử tử vào ngày 26/7/1644. Từ đây ngài chính là “Bông hoa đầu mùa” của Giáo Hội Việt Nam và Dòng Tên Việt Nam dâng lên Thiên Chúa. Chính vì lý do đó và nhằm ghi nhớ cách đặc biệt về ngày 18/1, lịch phụng vụ Dòng Tên đã lấy Chân phước Anrê Phú Yên mừng kính vào ngày này, nhằm đánh dấu sự kiện lần đầu tiên Dòng Tên có mặt tại Việt Nam.",-1),r=n("p",null,"Ngày 18/1 cũng chính là ngày bổn mạng của cộng đoàn Anrê Phú Yên, chúng con hân hoan vui mừng vì cộng đoàn nhỏ bé này có luôn có sự hiện diện của một bậc thánh nhân trẻ tuổi. Tiểu sử cuộc đời của Chân phước thật sự đã làm cho anh em chúng con cảm kích và thán phục. Ở độ tuổi 19, ngài cũng chạc tầm tuổi của anh em chúng con bây giờ, nhưng thật sự không thể so sánh được với những gì ngài đã làm được chỉ với số tuổi đó. Đến cái chết ngài cũng chẳng sợ, thì còn điều gì có thể làm ngài sợ nữa đây.",-1),y=n("p",null,"Hôm nay, được mừng kính nhớ đến ngài, được sống cùng tiểu sử của ngài, anh em chúng con thật sự đã có một được một ngày gọi là “Ngày Tình Yêu”. Tình yêu này đầu tiên xuất phát từ Thiên Chúa đã kêu gọi anh em chúng con để cùng sống và cùng đi. Tình yêu từ Quý Cha và Quý ân nhân đã và đang yêu thương chúng con. Chúng con sẽ không làm được gì nếu sống ngoài tình yêu của Thiên Chúa, chúng con chỉ biết dùng “tình yêu đáp lại tình yêu” mà thôi.",-1),C=JSON.parse('{"title":"Chân phước An-rê Phú Yên (18/01) - Bổn mạng cộng đoàn An-rê","description":"","frontmatter":{"title":"Chân phước An-rê Phú Yên (18/01) - Bổn mạng cộng đoàn An-rê","date":"2024-01-18T17:00:00.000Z","tags":["nhà ứng sinh","cộng đoàn"],"preview_image":"anre1.jpg","prev":{"text":"Ngày tri ân quý ân nhân của anh em ứng sinh miền Bắc","link":"/baiviet/20240115002-ngay-tri-an-quy-an-nhan-cua-anh-em-ung-sinh-mien-bac"},"next":{"text":"Lắng nghe","link":"/baiviet/20240226001-langnghe"}},"headers":[],"relativePath":"baiviet/20240119001-bon-mang-cong-doan-an-re-phu-yen.md","filePath":"baiviet/20240119001-bon-mang-cong-doan-an-re-phu-yen.md"}'),p={name:"baiviet/20240119001-bon-mang-cong-doan-an-re-phu-yen.md"},k=Object.assign(p,{setup(v){return(d,_)=>{const h=c("Figure");return t(),g("div",null,[i(h,{src:a(e),caption:"Cộng đoàn An-rê Phú Yên."},null,8,["src"]),o,s,u,m,l,r,y])}}});export{C as __pageData,k as default};
