import{D as t,o as i,c,I as g,m as a,k as n}from"./chunks/framework.3Y0MQAQP.js";const o="/assets/435891269_812846627540903_886548902243287485_n.m2HwKY1M.jpeg",e=n("p",null,"Với gần 5 thế kỷ tồn tại của mình, Dòng Tên đã cung cấp cho Giáo Hội những cộng tác viên thật xuất sắc.",-1),u=n("p",null,"Trong Công Đồng Trento có Claude Jay, Diego Lainez, Alfonso Salmeron, Peter Canisius và Polanco. Các Giêsu hữu được chú ý bởi việc định hướng cho Công đồng về những vấn đề trọng yếu, cụ thể như: quyền Đức Giáo Hoàng vốn liên hệ đến việc canh tân giáo triều; tương quan giữa Kinh Thánh và Truyền Thống; sự công chính hóa vốn liên hệ đến tội Nguyên Tổ; bản chất các bí tích, nhất là Bí tích Thánh Thể. Đặc biệt, với trực giác tuyệt vời của mình, Claude Jay đã nêu lên một nhận thức sâu rộng về sự cần thiết của việc đào tạo hàng giáo sĩ, và rồi, Công đồng đã có một sắc lệnh liên quan đến việc thiết lập chủng viện để huấn luyện linh mục tại các giáo phận-hình thức này vẫn được tiếp tục đến hôm nay.",-1),r=n("p",null,"Lại nữa, thần học và triết học hiện đại luôn phải nhắc đến các công trình của Pierre Teilhard de Chardin (1881-1955) và Henri de Lubac (1896-1991) của Pháp, Karl Rahner (1904-84) của Đức, Bernard Lonergan (1904-84) của Canada, John Courtney Murray (1904-67) của Hoa Kỳ, và Hans Urs von Balthasar (1905-88), người Thụy Sĩ. Tất cả đều là tu sĩ Dòng Tên. Những người khổng lồ về tâm trí này hiển nhiên thuộc hàng ngũ tiên phong của Công Đồng Vatican II và, trừ Teilhard de Chardin, người qua đời vào năm 1955, tất cả đều là những nhà thần học hàng đầu của Công Đồng này.",-1),l=n("p",null,"Lúc này đây, Thế giới của chúng ta là sân khấu của một cuộc chiến giữa cái tốt và cái xấu. Có những thế lực tiêu cực đang hoạt động, gây ra những tình huống bi đát, đẩy đưa anh chị em chúng ta vào vòng nô lệ tinh thần hay vật chất. Các thế lực đó thể hiện dưới nhiều hình thức, đặc biệt là ngang qua những khuynh hướng văn hóa ngày càng thống lĩnh: duy chủ quan, chủ nghĩa tương đối, duy khoái lạc, duy vật thực hành,…Những khuynh hướng này thường công kích vào những điểm nhức nhối trong giáo lý Giáo Hội Công Giáo. Những điểm nhức nhối thường được đề cập đến: ơn cứu độ của tất cả mọi người trong Đức Ki-tô, luân lý tính dục, hôn nhân và gia đình. Những chủ đề vừa nêu cần phải được đào sâu và soi sáng trong bối cảnh thực tại đương thời, với những nghiên cứu chuyên sâu về thần học, triết học, tâm lý và xã hội.",-1),s=n("p",null,"Với bối cảnh thế giới hiện tại như thế, Giáo Hội đang rất cần những con người có một đức tin sâu xa và vững chắc, một văn hóa nghiêm túc, một sự bén nhạy chân chính về nhân bản và xã hội. Giáo Hội cần những con người hiến dâng đời mình, đứng vào chính những giới tuyến đó để làm chứng và giúp con người hiểu rằng thực ra có một sự hài hòa sâu xa giữa đức tin và lý trí, giữa tinh thần Tin Mừng, khát vọng công lý và thực thi công bình.",-1),v=n("p",null,"Vậy, nếu bạn được Chúa ban cho một trí thông minh đặc biêt và muốn dùng tài năng Chúa ban để phục vụ Người và Hiền Thê của Người là Hội Thánh theo truyền thống ưu việt của Dòng Tên! Hãy đến với Dòng Tên, và bạn sẽ rất ngạc nhiên vì cách thế Dòng Tên sử dụng món quà quý giá nhất mà Chúa ban cho bạn.",-1),m=n("p",null,[n("em",null,"Lm. Giuse Ngô Văn Sơn, SJ")],-1),b=JSON.parse('{"title":"Tông đồ tri thức","description":"","frontmatter":{"title":"Tông đồ tri thức","date":"2024-04-11T17:00:00.000Z","tags":["tông đồ","tri thức","ơn gọi"],"preview_image":"435891269_812846627540903_886548902243287485_n.jpeg","prev":{"text":"Tông đồ người nghèo","link":"/baiviet/20240405001-tong-do-nguoi-ngheo"},"next":{"text":"Tông đồ tri thức","link":"/baiviet/20240421001-tong-do-truyen-giao"}},"headers":[],"relativePath":"baiviet/20240412001-tong-do-tri-thuc.md","filePath":"baiviet/20240412001-tong-do-tri-thuc.md"}'),d={name:"baiviet/20240412001-tong-do-tri-thuc.md"},C=Object.assign(d,{setup(_){return(p,y)=>{const h=t("Figure");return i(),c("div",null,[g(h,{src:a(o),caption:"Tông đồ tri thức."},null,8,["src"]),e,u,r,l,s,v,m])}}});export{b as __pageData,C as default};
