import{_ as h,o as i,c as t,k as n}from"./chunks/framework.3Y0MQAQP.js";const C=JSON.parse('{"title":"Ngày 1: Cú Lội Ngược Dòng Để Đời Của Chàng Thanh Niên Inhaxiô Thành Loyola","description":"","frontmatter":{"title":"Ngày 1: Cú Lội Ngược Dòng Để Đời Của Chàng Thanh Niên Inhaxiô Thành Loyola","date":"2024-07-21T17:00:00.000Z","tags":["I-nhã 2024","thiêng liêng","nhà ứng sinh"]},"headers":[],"relativePath":"baiviet/20240722001-loi-nguoc-dong.md","filePath":"baiviet/20240722001-loi-nguoc-dong.md"}'),c={name:"baiviet/20240722001-loi-nguoc-dong.md"},g=n("p",null,"Trong vườn hoa các thánh và chân phước Dòng Tên nói riêng và trong Giáo Hội nói chung, có một vị thánh rất nổi bật và sáng giá, đó là thánh Inhaxiô thành Loyola. Ngài là Đấng sáng lập ra một Dòng nhỏ bé mang tên gọi là Dòng Chúa Giêsu hay còn gọi là Dòng Tên theo văn hóa Việt Nam. Chắc hẳn cũng giống như biến cố ngã ngựa của thánh Phaolô mà thánh nhân mới được ơn hoán cải và nhận ra tiếng Chúa gọi mời để phục vụ Người. Cả cuộc đời của thánh Inhaxiô thành Loyola cũng là một chuỗi hành trình hoán cải giống như vậy, nhờ biến cố bị thương tại Pamplona, từ một chàng hiệp sĩ chạy theo những phù phiếm thế gian, trở nên người bạn đường thiết thân với Chúa Giêsu Kitô trong việc phục vụ Giáo Hội nhằm tôn vinh danh Chúa.",-1),a=n("p",null,"Thánh Inhaxiô sinh ra vào năm 1491 trong một gia đình quyền quý Tây Ban Nha ở xứ Basque. Ngài là con út trong gia đình có mười ba người con. Thánh nhân được huấn luyện trong một gia đình có truyền thống hiệp sĩ nhằm trung thành phục tùng triều đình Tây Ban Nha bấy giờ. Bởi thế, ngay từ thời niên thiếu, Inhaxiô được nhà vua chọn làm người hầu cận, tiếp đến là chiến sĩ trong quân đội hoàng gia. Năm 1517, Inhaxiô đến xứ Navarre ở phía bắc Tây Ban Nha tòng quân cho công tước thành Najera và và phó vương thành Navarre phụ trách việc quốc phòng với mục đích là được thăng tiến thành một tướng quân. Dưới sự lãnh đạo của công tước thành Najera, Inhaxiô đã tham gia nhiều trận đánh mà không bị thương tích gì. Tuy nhiên, cuộc đời Inhaxiô đã thay đổi hoàn toàn vào năm 1521 trong một trận chiến tại thành Pamplona chống lại quân Pháp, Inhaxiô bị thương nặng khi một quả đạn pháo đánh trúng chân người, một chân bị gãy còn chân kia bị thương nặng do vết đạn, khiến ngài phải nằm dưỡng bệnh trong thời gian dài sau nhiều lần phẫu thuật trong đau đớn.",-1),m=n("p",null,'Xuyên suốt khoảng thời gian hồi phục tại lâu đài gia đình ở Loyola, Inhaxiô đã trải qua một bước ngoặt quan trọng trong cuộc đời để tìm ra một hướng đi mới cho tương lai của mình. Inhaxiô vốn dĩ là một chàng hiệp sĩ hào hoa nên chàng yêu thích đọc những cuốn tiểu thuyết về chủ đề kiếm hiệp lãng mạn. Tuy nhiên, trong lâu đài bấy giờ, chàng không thể tìm thấy những cuốn sách ấy, thế là chàng miễn cưỡng đọc một vài cuốn sách thiêng liêng có sẵn về "Cuộc đời Chúa Giêsu" và "Hạnh các Thánh". Ban đầu đọc những tác phẩm này, chàng cảm thấy buồn chán, nhưng sau đó dưới sự tác động của Thiên Chúa, Inhaxiô dần dần bị cuốn hút bởi nội dung của chúng. Khi chàng suy ngẫm về cuộc đời các thánh và những lời giáo huấn của Chúa Giêsu, chàng bắt đầu trải qua một sự biến chuyển nội tâm sâu sắc. Từ một chàng hiệp sĩ đam mê những chuyến phù phiếm, Inhaxiô nhận thấy mình bị thu hút bởi sự đơn giản, khiêm nhường và lòng mộ đạo của các thánh, nhất là thánh Phanxicô Assisi cùng thánh Đaminh. Và chàng khao khát cũng muốn trở thành một vị thánh mà Thiên Chúa hằng mong ước giống như gương bao vị thánh mà chàng đã đọc qua. Chàng bắt đầu đặt những câu hỏi về những tham vọng thế tục trước đây của mình và ý nghĩa của cuộc đời bản thân với những chuyển động nơi nội tâm chàng. Nội tâm chàng sầu khổ khi mải mơ tưởng những thứ đam mê hão huyền và lý tưởng hiệp sĩ phù hoa trước đây của mình và nội tâm chàng an ủi khi mãi suy gẫm về việc phục vụ Chúa Giêsu Kitô với tư cách là một hiệp sĩ của Người. Sau cùng, Inhaxiô nhận ra rằng hạnh phúc đích thực không nằm ở những hư danh thế tục mà nằm ở việc phục vụ Thiên Chúa nhằm tôn vinh danh Người.',-1),o=n("p",null,'Sau khi các vết thương lành lặn, Inhaxiô đưa ra quyết định táo bạo từ bỏ sự nghiệp hiệp sĩ trước đây và bắt đầu một cuộc hành trình thiêng liêng hay nói đúng hơn là một cuộc phiêu lưu cùng với Chúa Giêsu trong hành trình chinh phục nước Chúa. Chàng nhanh chóng đi đến tu viện thuộc dòng Biển Đức ở Montserrat, và chính tại nơi đây, chàng đã thực hiện một cuộc đại xưng tội về lịch sử tội đời mình với một cha Dòng Biển Đức như một biểu tượng của sự từ bỏ và hoán cải. Sau đó, Inhaxiô dâng kiếm thế lên bàn thờ Đức Mẹ và thay đổi quần áo đẹp của mình bằng y phục đơn giản nghèo hèn của một người hành hương rồi lên đường tới thị trấn Manresa để cầu nguyện, hoán cải và suy ngẫm cuộc đời mình trong một cái hang cô tịch gần một năm. Chính trong giai đoạn hoán cải đặc biệt này, Inhaxiô đã có những cái nhìn sâu sắc về hướng đi thiêng liêng mới của cuộc đời mình và được Chúa xác chuẩn bằng những thị kiến sâu sắc. Inhaxiô đã ghi lại những kinh nghiệm cá nhân được Chúa soi sáng này trong một cuốn sổ tay, sau này được biên soạn thành tác phẩm kinh điển mang tên "Linh Thao". Tác phẩm này, bao gồm một loạt các bài tập thao luyện thiêng liêng, cầu nguyện suy niệm, chiêm niệm, đã trở thành nền tảng của linh đạo Inhaxiô và đã hướng dẫn không ít người trên con đường nên thánh.',-1),u=n("p",null,"Sau đó, vào năm 1523, Inhaxiô rời Manresa để khởi hành đi hành hương Giêrusalem. Chính ở nơi đây, ngài đã sống nhờ việc khất thực và hăng say hoán cải người Hồi Giáo nhờ kinh nghiệm Linh Thao tại Manresa. Tuy nhiên, thời gian phục vụ của Ngài tại Đất Thánh không được lâu và vì lo sợ cho tính mạng của ngài, nên Cha Bề Trên Phanxicô ở Giêrusalem khuyên ngài trở về Barcelona để phục vụ. Sau khi từ Đất Thánh trở về, Inhaxiô quyết định trở thành một linh mục. Tin tưởng rằng để giúp đỡ tha nhân một cách hiệu quả nhất thì phải có kiến thức uyên bác và thâm sâu. Ngài đã quyết tâm dành mười một năm tiếp đó cho việc học ở Alcalá, Salamanca, rồi đến Paris. Việc này đòi hỏi ngài phải nỗ lực học hành nghiêm túc và phải hy sinh rất nhiều, dù tuổi tác của ngài lúc ấy cũng đã khá cao. Thật vậy, Inhaxiô đã cố gắng học tiếng La tinh và may mắn ghi tên vào học ở Đại học Paris trứ danh lúc bấy giờ. Trong lúc theo học ở Paris, Inhaxiô cũng đã “thu phục” được một nhóm nhỏ sinh viên gồm thánh Phanxicô Xaviê và thánh Phêrô Favre, và chính ngài giúp làm Linh Thao cho họ. Sau đó, nhóm bạn này kết nạp thêm một vài thành viên nữa để rồi đi đến việc chính thức lập nhóm bạn đường đầu tiên của Inhaxiô trong biến cố tất cả các thành viên trong nhóm tuyên khấn giữ ba khuyên Phúc m tại đồi Montmartre. Biến cố này là cột mốc quan trọng cho việc khai sinh ra Dòng Tên vào ngày 27.09.1540 dưới sự quản trị của vị bề trên cả đầu tiên Dòng Tên là cha thánh Inhaxiô.",-1),l=n("p",null,"Cuộc hoán cải thật sự là một điều không hề dễ dàng bởi vì hành trình này đòi hỏi con người ta phải bỏ mình và thanh luyện bản thân liên tục nhằm hiểu biết Chúa hơn, yêu mến Chúa hơn, va theo sát Chúa hơn. Nhìn vào gương hoán cải của thánh Inhaxiô Loyola, ta thấy ngài như là một minh chứng sống động cho sức mạnh của lòng tin và ý chí dành cho việc phụng sự Thiên Chúa. Từ một chàng thanh niên ngạo mạn, một hiệp sĩ mải mê danh vọng phù phiếm, Inhaxiô giờ đây trở thành một vị thánh nổi danh của Hội Thánh, một nhà thần bí có tầm vóc trong lịch sử Giáo Hội, và một vị Bề Trên Cả Dòng Tên mẫu mực với tâm hồn khiêm nhường và trí tuệ sâu sắc. Bên cạnh việc có công lớn trong việc sáng lập ra Dòng Tên, câu chuyện hoán cải của thánh nhân cũng là tiền đề cho việc ra đời quyển Linh Thao như là một di sản lớn của Giáo Hội ảnh hưởng sâu rộng đến nền linh đạo Kitô giáo. Và cũng chính nhờ Linh Thao, Giáo Hội mới có thêm nhiều vị thánh, mà điển hình là thánh Phanxicô Xaviê và thánh Gioan Berchmans.",-1),r=n("p",null,[n("em",null,"Lạy Chúa, Chúa đã làm những việc lạ lùng nơi cuộc đời thánh I-nhã. Đó là khơi lên trong lòng thánh nhân khao khát tìm chân lý và niềm vui nơi Chúa, nhờ đó, dần dần, thánh nhân đã tìm ra được kho tàng cao quý nhất là chính Chúa, và chiến đấu cho tới hơi thở cuối cùng để chiếm lấy cho kì được. Xin cho chúng con đang khi ở dưới thế này, cũng luôn tìm thi hành điều Chúa muốn nơi chúng con, để cuộc đời chúng con làm vinh danh Chúa luôn luôn. Amen.")],-1),s=[g,a,m,o,u,l,r];function v(y,p,e,b,d,k){return i(),t("div",null,s)}const x=h(c,[["render",v]]);export{C as __pageData,x as default};
