import{D as i,o as t,c,I as g,m as a,a as u,k as n}from"./chunks/framework.3Y0MQAQP.js";const o="/assets/ngay6_inha_2024.t4_7j2i9.jpeg",m=n("p",null,"Mở đầu là biến cố Pamplona. Đây là khởi điểm của hành trình khám phá những điều mới trong cuộc đời của ngài với cái nhìn của Chúa. Qua biến cố này, ngài đã có thời gian dừng lại. Qua việc đọc các sách về cuộc đời Chúa Giê-su và các Thánh, ngài đã có dịp nhìn lại cuộc đời hiện tại của mình, là cuộc đời của vị hiệp sĩ với đầy những mơ mộng… Ngài nhận ra tất cả mọi sự đều là phù du khi đôi chân không còn lành lặn. Qua biến cố đó, Thiên Chúa đã khơi lên trong lòng thánh nhân niềm khát khao kiếm tìm ý Chúa. Nhờ đó, ngài bắt đầu đi tìm con đường đến với Chúa ngang qua việc bắt chước những việc mà các Thánh đã làm. Ngài đã giữ cách nghiêm khắc kỷ luật, hãm mình, phạt xác, xưng tội… Tuy vậy, ngài lại không tìm thấy hạnh phúc nơi những việc làm ấy.",-1),l=n("p",null,"Sau đó, Thiên Chúa đã tỏ lộ cho ngài qua ơn soi sáng ở bờ sông Cardoner. Biến cố này đã thay đổi hoàn toàn con người của ngài, từ nội tâm đến thiêng liêng. Ngài đã kiên quyết từ bỏ lối sống truy cầu danh vọng, hiệp sĩ… sang đời sống truy tìm ý Chúa trong mọi sự, với kinh nghiệm thấy “mọi sự đều mới”. Cũng qua biến cố này, thánh nhân cũng có cái nhìn mới về đời sống thiêng liêng của mình. Thánh I-nhã đần nhận ra tất cả những điều mà ngài đang làm, hay cố gắng bắt chước theo các thánh, cùng lối sống hãm mình, kỷ luật trước đây của mình không phải là điều cốt yếu của đời sống thiêng liêng, tự nó không có giá trị tốt đẹp nào để theo đuổi, nếu chúng không được đặt dưới sự hướng dẫn của Chúa, nghĩa là một sự bỏ mình bên trong, bỏ đi cái tôi, tính duy ý chí, mà để cho Chúa hướng dẫn mình bằng ân sủng của Ngài. Vì đến với Chúa, người ta không thể chỉ dùng “lý trí” mà đạt được. Trái lại, khi càng buông mình trong niềm tín thác, thì lại càng “thấu hiểu” nhiều điều hơn về Chúa. Thiên Chúa cũng dạy cho I-nhã nhiều bài học về kinh nghiệm phân định thần loại. Nhờ đó thánh nhân đã giúp đỡ nhiều người đến gặp ngài trong những việc thiêng liêng.",-1),s=n("p",null,"Chính những điều Thiên Chúa tác động vào cuộc đời của I-nhã đã làm nên sự thay đổi tận căn nơi cuộc đời ngài. Thị kiến tại La Storta như một sự phê chuẩn cho ước muốn phục vụ Chúa và giúp đỡ các linh hồn của Ngài. Ngang qua đó, ngài trở thành một tông đồ của Chúa, và cũng từ đó, một dòng tông đồ đầu tiên mang tên Giêsu dần được hình thành. Nhìn lại đời sống của I-nhã, chúng ta thấy rõ sự kết hợp thâm sâu của ngài với Thiên Chúa, được ghi chép lại trong tự thuật của ngài. Hay rõ hơn là qua lời kinh dâng hiến, qua tập linh thao và qua hiến pháp của dòng, ngài đã viết lên những điều ngài đã nhận ra trong lúc ngài sống ở Manresa. Cha Nadal, người bạn thân tín nhất của ngài, thuật lại rằng mỗi khi có ai hỏi I-nhã tại sao lại đưa điểm này hay điều nọ vào Hiến Pháp, thì mười lần như một ngài sẽ trả lời rằng: “Điều đó tương ứng với một chuyện đã xảy cho tôi tại Manresa”. Điều này nói lên rằng Thiên Chúa đã làm việc trên cuộc đời ngài. Nhờ quyền năng Chúa, I-nhã lớn lên và trưởng thành trong đời sống nội tâm và thiêng liêng. Ngài nhận ra ý Chúa trong mọi sự, và quyết dâng hiến trọn thân xác và linh hồn mình cho Chúa.",-1),r=n("p",null,[n("em",null,"Lạy Thánh I-nhã, xin cầu bầu cho chúng con biết nhìn mọi sự dưới cái nhìn yêu thương, mới mẻ của Chúa, nhờ vậy chúng con nhận ra hình ảnh Ngài trong mọi sự, để biết bao dung và đón nhận mọi sự xảy đến với mình trong bình an, hầu “cho vinh danh Chúa hơn”. Amen.")],-1),k=JSON.parse('{"title":"Ngày 6: Con người của chiều sâu nội tâm và thiêng liêng","description":"","frontmatter":{"title":"Ngày 6: Con người của chiều sâu nội tâm và thiêng liêng","date":"2024-07-26T17:00:00.000Z","tags":["I-nhã 2024","thiêng liêng","nhà ứng sinh"]},"headers":[],"relativePath":"baiviet/20240727001-con-nguoi-cua-chieu-sau-noi-tam.md","filePath":"baiviet/20240727001-con-nguoi-cua-chieu-sau-noi-tam.md"}'),e={name:"baiviet/20240727001-con-nguoi-cua-chieu-sau-noi-tam.md"},C=Object.assign(e,{setup(v){return(y,p)=>{const h=i("Figure");return t(),c("div",null,[g(h,{src:a(o),caption:"Ngày 6."},null,8,["src"]),u(" Khi nhắc đến Thánh I-nhã, người ta không thể không biết đến ngài là một con người có chiều sâu nội tâm và thiêng liêng. Để nói về điều này thì không thể không nhắc đến các biến cố trong cuộc đời của thánh nhân. Qua đó, chúng ta thấy bàn tay Thiên Chúa làm việc trên cuộc đời của ngài. "),m,l,s,r])}}});export{k as __pageData,C as default};
