import{_ as h,o as c,c as t,k as n,a as i}from"./chunks/framework.AlgW7ztY.js";const T=JSON.parse('{"title":"Phục vụ từ những điều giản đơn","description":"","frontmatter":{"title":"Phục vụ từ những điều giản đơn","date":"2023-10-14T17:00:00.000Z","tags":["tông đồ"],"prev":{"text":"\\"Chúng ta là tội nhân và cần sự tha thứ\\"","link":"/chiase/20231001001-chung-ta-la-toi-nhan-va-can-su-tha-thu"},"next":{"text":"Thánh Alphonso Rodriguez (31/10) - Bổn mạng cộng đoàn Vườn Dâu 1","link":"/chiase/20231031001-bon-mang-cong-doan-vuon-dau-1"}},"headers":[],"relativePath":"chiase/20231015001-phuc-vu-tu-nhung-dieu-gian-don.md","filePath":"chiase/20231015001-phuc-vu-tu-nhung-dieu-gian-don.md"}'),g={name:"chiase/20231015001-phuc-vu-tu-nhung-dieu-gian-don.md"},a=n("p",null,[n("em",null,"“Ứng sinh phải là người có trái tim và tinh thần tông đồ, trước hết bằng chính việc siêng năng học hành, tập suy nghĩ, nhận định, sau mới là những kiến thức và kĩ năng tông đồ. Khi có dịp thích hợp và trong một số việc thích hợp ứng sinh sẽ học tập cách làm việc tông đồ.”"),i(" (NSƯS số 59)")],-1),o=n("p",null,"Là một người ứng sinh dòng Tên chắc hẳn ai cũng đã trải qua những kinh nghiệm tông đồ vì đây là một trong năm phương diện đào luyện người ứng sinh dòng Tên cách riêng và xa hơn là người Giêsu hữu cách chung. Có rất nhiều việc tông đồ như đi dạy học, hát lễ, cắt tóc cho các bác các chú trong viện tâm thần, đóng nước giúp các sơ. Riêng tôi thì tôi được đi tông đồ tại nhà dưỡng lão tình thương Vinh sơn tại Bình Lợi.",-1),u=n("p",null,"Vào sáng thứ bảy hằng tuần, nhóm chúng tôi gồm năm người được chọn đi làm tông đồ tại Bình Lợi. Nơi đây là nhà dưỡng lão chuyên giúp đỡ cho các cụ bà không còn nơi nương tựa hoặc những cụ đang trong tình cảnh khó khăn.",-1),s=n("p",null,"Công việc của chúng tôi hằng ngày là đẩy các cụ trên xe lăn ra ngoài trời để tắm nắng, chia sẻ Lời Chúa cho các cụ, trò chuyện cũng như giúp đỡ các cụ trong việc đi lại. Tôi còn ấn tượng với hình ảnh hôm đó là ngày tổ chức sinh nhật cho một số cụ trong nhóm, trên tay tôi cầm khay đồ ăn đi đến từng cụ và mời họ. Hình ảnh đó đã đánh động tôi rất nhiều về sự phục vụ. Một công việc tưởng như là bình thường nhưng rất cần sự ân cần, nhẹ nhàng, một hành động đòi hỏi người phục vụ phải cúi thấp mình xuống cả trong tư thế và nơi trái tim. Như thế sự phục vụ được khởi đi từ con tim đến đôi tay để rồi trao đi món quà.",-1),m=n("p",null,"Đối với tôi, mỗi lần bắt gặp các cụ từ phía xa xa tôi luôn nhận thấy các cụ rất vui vẻ, hào hứng như đang mong chúng tôi đến vậy. Chính điều này không chỉ mang đến cho tôi cảm nhận mình được chào đón mà còn thấy sự gần gũi giữa tôi và các cụ. Thật tuyệt vời khi trao đi cũng đồng thời là được nhận lại. Khi chúng tôi trao đi niềm vui trên nét mặt thì chúng tôi cũng được nhận về những nụ cười từ các cụ. Hay việc hỏi thăm các cụ, đẩy xe lăn, giúp các cụ đi lại cũng làm cho các cụ vui lòng rồi. Rất nhiều những công việc tưởng trừng như đơn giản nhưng lại giúp tôi tập tành làm những việc nhỏ với sự chuyên tâm. Nói đến đây tôi nhớ đến Thánh nữ Têrêsa Hài đồng Giêsu đã nên thánh qua những việc nhỏ bé, tầm thường. Hay như mẹ Têrêsa Calcuta đã từng nói “Làm những việc nhỏ bé với một tình yêu lớn lao.”",-1),l=n("p",null,"Có những lúc tôi nghĩ tuổi già là tuổi rất cần người thân hay con cháu bên cạnh. Qua trò chuyện với các cụ tôi mới biết mỗi cụ có những hoàn cảnh riêng. Có nhiều cụ ngay từ nhỏ đã không còn người thân, không còn nơi nương tựa. Có cụ chia sẻ trước khi vào sống trong nhà dưỡng lão này các cụ đã phải tự thân tự lập sống bươn trải, làm đủ mọi việc để kiếm sống, cuộc sống rày đây mai đó, lây lất giữa chợ đời. Cứ như thế cho đến khi các cụ không còn đủ sức khỏe để tự lo cho bản thân thì khi đó bà con lối xóm quen biết nhà dưỡng lão tình thương Vinh sơn giới thiệu và đưa cụ vào môi trường mới đầy đủ và an toàn này. Ngoài ra, có những cụ may mắn vẫn còn người thân nhưng việc thăm nom cũng không được thường xuyên.",-1),r=n("p",null,"Đó là một chút cảm nghiệm tông đồ của riêng tôi và chắc hẳn mỗi người tại những điểm tông đồ khác nhau sẽ có những trải nghiệm tông đồ mang hương vị và màu sắc riêng. Nhưng tất cả những việc làm này là để nhằm mục đích chuẩn bị cho người ứng sinh có thể trở thành người tông đồ phục vụ đắc lực Nước Trời, theo cách thế của dòng Tên.",-1),e=[a,o,u,s,m,l,r];function v(p,d,y,_,k,b){return c(),t("div",null,e)}const N=h(g,[["render",v]]);export{T as __pageData,N as default};
