import{D as c,o as g,c as t,I as o,m as a,k as n}from"./chunks/framework.iYlBf-hB.js";const i="/assets/unnamed.ba2l6lcg.png",e=n("p",null,"Alphonso sinh ngày 25 tháng 7 năm 1532 ở Segovia, Tây Ban Nha, con của một người buôn bán len sợi giầu có. Khi còn nhỏ, Alphonso thường gặp Cha Peter Favre (sau này được phong chân phước) và một linh mục dòng Tên khác, họ là những người thường tạm trú qua đêm ở nhà cha của Alphonso. Dần dà, chính Cha Peter Favre là người đã chuẩn bị cho Alphonso rước lễ lần đầu.",-1),u=n("p",null,"Suốt đời thầy Anphong Rodriguez chưa bao giờ đặt chân đến Rôma. Nhưng ngày 15/01/1888, mãi 357 năm sau khi thầy chào đời, thầy đã được đón tiếp trọng thể hơn bất kỳ vị tướng lãnh nào trở về Rôma sau khi chiến thắng khải hoàn. Buổi sáng Chúa Nhật hôm đó, Đức Lêô XIII long trọng ghi tên thầy Anphong Rodriguez vào sổ bộ các thánh.",-1),s=n("p",null,"Ngày 31/10/2023, Cha chủ tế Gioan Phạm Duy Anh, SJ cùng thầy quản lý phó tế Giuse Ngô Văn Sơn, SJ củng là Thầy đồng hành của Cộng đoàn cùng với Quý Ân Nhân và các Anh Em ứng sinh đã hiệp dâng thánh lễ mừng Bổn mạng của Cộng đoàn Cộng đoàn Alphonsus Rodriguez tại nhà nguyện của Cộng đoàn để dâng lời cầu nguyện Quý ân nhân, thân nhân và cách đặc biệt cầu nguyện mỗi anh em đang sống trong cộng đoàn luôn rực cháy ngọn lửa của lòng hiệp nhất và tận tâm trong việc phục vụ Chúa để mỗi ngày các anh em thêm dấn thân cách mạnh mẽ hơn cho ơn gọi Giê-su Hữu trong tương lai.",-1),l=n("p",null,[n("em",null,"Lạy Cha, qua đời sống trung tín của vị bảo hộ Cộng Đoàn chúng con là thánh Anphong Rodriguez, Cha cho chúng con thấy con đường dẫn đến niềm vui và bình an. Xin cho chúng con được làm người bạn đường nghĩa thiết của Đức Giêsu Kitô, Đấng đã tự nguyện làm đầy tớ cho mọi người, và nay đang sống và hiển trị với Cha và Chúa Thánh Thần muôn đời.")],-1),y=JSON.parse('{"title":"Ngày 31/10, Thánh Alphonso Rodriguez S.J. - Bổn mạng cộng đoàn Vườn Dâu 1","description":"","frontmatter":{"title":"Ngày 31/10, Thánh Alphonso Rodriguez S.J. - Bổn mạng cộng đoàn Vườn Dâu 1","date":"2023-10-30T17:00:00.000Z","preview_image":"unnamed.png?w=900","category":"congdoan","prev":{"text":"Phục vụ từ những điều giản đơn","link":"/chiase/20231015001-phuc-vu-tu-nhung-dieu-gian-don"},"next":{"text":"Tri Thức Nâng Cánh Ước Mơ","link":"/chiase/20231118001-tri-thuc-nang-canh-uoc-mo"}},"headers":[],"relativePath":"chiase/20231031001-bon-mang-cong-doan-vuon-dau-1.md","filePath":"chiase/20231031001-bon-mang-cong-doan-vuon-dau-1.md"}'),m={name:"chiase/20231031001-bon-mang-cong-doan-vuon-dau-1.md"},_=Object.assign(m,{setup(r){return(d,p)=>{const h=c("Figure");return g(),t("div",null,[e,u,o(h,{src:a(i),caption:"Cộng đoàn Vườn Dâu 1."},null,8,["src"]),s,l])}}});export{y as __pageData,_ as default};
