import{_ as t,o as h,c as i,k as n}from"./chunks/framework.AlgW7ztY.js";const T=JSON.parse('{"title":"Tri Thức Nâng Cánh Ước Mơ","description":"","frontmatter":{"title":"Tri Thức Nâng Cánh Ước Mơ","date":"2023-11-07T17:00:00.000Z","tags":["tri thức"],"prev":{"text":"Thánh Alphonso Rodriguez (31/10) - Bổn mạng cộng đoàn Vườn Dâu 1","link":"/chiase/20231031001-bon-mang-cong-doan-vuon-dau-1"},"next":{"text":"Chân phước Miquel Augustin Pro (23/11) - Bổn mạng cộng đoàn ứng sinh miền Bắc","link":"/chiase/20231127001-bon-mang-cong-doan-mien-bac"}},"headers":[],"relativePath":"chiase/20231118001-tri-thuc-nang-canh-uoc-mo.md","filePath":"chiase/20231118001-tri-thuc-nang-canh-uoc-mo.md"}'),c={name:"chiase/20231118001-tri-thuc-nang-canh-uoc-mo.md"},g=n("p",null,"Trong cuộc sống tất bật và xô bồ đã có biết bao lần ta trăn trở “tri thức” là gì mà từ thế hệ cha ông chúng ta vẫn luôn theo đuổi tri thức đến tận ngày hôm nay, dẫu biết rằng “đời sống có hạn nhưng kiến thức là vô hạn.” Ai đó đã từng nói rất hay rằng “tri thức là con mắt của đam mê, và có thể trở thành hoa tiêu trong tâm hồn.” Vậy bản thân chúng ta đã làm gì? Có mục tiêu như thế nào để đạt được tới đích đó? Khi ta thấy chán nản, thất vọng hay đang gặp những gian lao thử thách hãy nhớ tới cội nguồn mà ta bắt đầu thực hiện.",-1),o=n("p",null,"Việc học tập cũng thế, chỉ khi bản thân mỗi người biết mục tiêu trong học tập thì mới có thể quyết tâm chinh phục nó bằng quyết tâm, sự nỗ lực không ngừng nghỉ. Chinh phục nó để hoàn thiện ước mơ, hoàn thiện bản thân và vững vàng trong cuộc sống từ bỏ những thanh cao không phải là của mình “Bộ lông làm đẹp con công, học vấn làm đẹp con người.”( Ngạn Ngữ Nga)",-1),a=n("p",null,"Kiến thức cũng không tự nhiên mà có, cũng không tự nhiên mà mất đi đó là quá trình tích lũy không ngừng nghỉ, trau dồi từng ngày và những trải nghiệm sâu sắc ta đã thu lượm được trong đời sống sinh hoạt thường ngày. Đối với một ứng sinh trong giai đoạn huấn luyện, việc học vô cùng quan trọng vì nó là điều kiện tiên quyết và là nền tảng để tôi có thể trở thành một người Tông Đồ cho Dòng và Giáo Hội trong tương lai.",-1),u=n("p",null,"Trong thông điệp đầu tay của mình, Đức Giáo Hoàng Biển Đức XVI đã viết: “Là Ki-tô Hữu không phải là kết quả của một lựa chọn đạo đức hay một ý tưởng cao siêu, nhưng là gặp gỡ với một biến cố, một con người, sự gặp gỡ đem đến cho cuộc đời một chân trời mới và một hướng đi quyết định.” Một Giê-su Hữu không ngừng trang bị kiến thức cho bản thân để từ đó mang những gì mà mình đã có để đến gặp gỡ, cho đi và phục vụ mọi người trên khắp thế giới.",-1),m=n("p",null,"Anh em mình đến từ khắp mọi nơi trên khắp thế giới được quy tụ bởi Vị Thủ Lĩnh là chính Chúa Giê-su, chính Ngài đã gọi chọn gọi anh em mình trong đời sống dâng hiến dù học đủ mọi các chuyên ngành khác nhau, bên chính trị xã hội hay văn hóa nghệ thuật. Có người hỏi rằng: Đi tu có vui không? Câu trả lời của tôi rất đơn giản: “Rất vui! vì ơn gọi đời tôi như một hạt giống đã được Chúa gieo vào lòng đất rồi nhờ công vun tưới của bao người mà được nên hình dạng.”",-1),r=n("p",null,"Thật là tuyệt đẹp của tuổi thanh xuân khi chúng mình được học tập trong môi trường của nhà ứng sinh vừa được sống với những anh em trong cộng đoàn.",-1),e=n("p",null,[n("em",null,"— Cộng Đoàn Vân Canh—")],-1),s=[g,o,a,u,m,r,e];function l(p,v,d,_,k,b){return h(),i("div",null,s)}const C=t(c,[["render",l]]);export{T as __pageData,C as default};
