import{D as g,o as t,c as i,I as c,m as a,k as n}from"./chunks/framework.iYlBf-hB.js";const e="/assets/428629145_784613127030920_6798862236280608071_n.NJpFGbgk.jpeg",m=n("p",null,[n("em",null,"“Lạy Chúa, xin cho con một con tim biết lắng nghe” (1V 3, 9).")],-1),l=n("p",null,"Trong đời sống cộng đoàn, tình yêu thương anh em luôn xuất phát từ lắng nghe và thấu hiểu. Để yêu thương từng người anh em trong cộng đoàn ta cần làm gì? Ta cần hiểu, chia sẻ và cảm thông với từng người anh em. Vậy ta cần làm gì để hiểu được? Ta cần có một con tim biết lắng nghe.",-1),o=n("p",null,"Chất lượng của tình yêu thương được bộc lộ qua chất lượng của việc lắng nghe. Thực ra, nghe không phải là chuyện dễ dàng: lắng nghe, cũng có thể là làm người khác đang nói phải dừng lại, bởi có những người nói đủ mọi thứ vì một mối âu lo nào đó. Lắng nghe bao hàm cả một nghệ thuật. Lắng nghe, là dành cho anh em một không gian nội tâm của ta để tiếp nhận anh em, tiếp nhận lời của anh em, tiếp nhận điều anh em nói ra. Lắng nghe ai đó, chính là để cho người đó đi vào nội tâm của mình. Một trong những điều cần tránh khi một người nêu lên một vấn đề, đó là đưa ra các giải pháp. Khi đang có cơ hội, ta luôn có khuynh hướng đưa ra, hay có ý muốn đưa ra các giải pháp, trong khi đó trên thực tế, điều mà người đó muốn lại không phải là một giải pháp, mà là một sự cảm thông. Đương sự muốn nghe ta nói: “Tôi không có giải pháp, nhưng điều anh đang sống quả là quan trọng và tôi sẵn sàng chia sẻ với anh”. Đưa ra một giải pháp có thể là một sự xua đuổi: “Hãy tới bác sĩ khám xem, hãy tới gặp những người này, hãy làm như thế này,…, hãy làm như thế”. Điều anh em cần, đó là tình bằng hữu, sự chia sẻ và thái độ lắng nghe. Và khi chúng ta sống với nhau, chúng ta cần cầu xin ơn này, ơn khôn ngoan để lắng nghe và học hỏi để hiểu được ngôn ngữ của người khác. Vấn đề là hiểu được ngôn ngữ của anh em vốn không phải ngôn ngữ bằng lời nói, mà là bằng tiếng hét, ngôn ngữ của đôi mắt, ngôn ngữ của gương mặt.",-1),s=n("p",null,"Trong mỗi cộng đoàn đều có ngôn ngữ riêng của cộng đoàn đó. Một cộng đoàn đang gặp khó khăn hay khủng hoảng gì đó, thì không cần phải bề trên hay trưởng cộng đoàn đứng lên tuyên bố hay nói ra rõ ràng thì mọi người mới biết được. Nhưng có thể nhận biết với ngôn ngữ riêng với con tim biết lắng nghe. Thử hình dung, trong cộng đoàn có bất kì anh em nào đang gặp khó khăn về sức khỏe như cảm cúm với biểu hiện là ho và hắt xì. Tự bản chất tiếng ho và tiếng hắt xì sổ mũi cũng là một ngôn ngữ cần được lắng nghe. Chính qua tiếng ho hay tiếng hắt xì sổ mũi này mà người anh em có thể khám phá ra mình có được yêu thương hay không. Tiếng ho, tiếng hắt xì sụt sịt đó có làm phiền tới ta? Hay ta có hiểu được tiếng ho, tiếng hắt xì đó không? Người anh em đó có thực sự quan trọng đối với ta? Và qua đó, người anh em có thể khám phá ra mình đang sống với những người bạn như gia đình hay như người ở cùng trọ.",-1),u=n("p",null,"Do đó, để có thể bước chân vào cuộc đời của nhau, và từ những người xa lạ nên một gia đình như gia đình Nadarét thì cần có một con tim biết lắng nghe. Như trong Nếp sống ứng sinh số 6: “Chính gia đình thánh của Chúa Giêsu ở Nadarét là khuôn mẫu sống động nhất của Nhà Ứng sinh Dòng Tên.” Và “Các ứng sinh cũng tìm sống mầu nhiệm Nadarét trong khung cảnh Nhà Ứng sinh”. Chính nhờ việc lắng nghe, anh em thấu hiểu và chấp nhận nhau. Nhờ đó anh em đồng chí hướng và tâm hồn với Đức Kitô hơn.",-1),r=n("p",null,[n("em",null,"---Cộng Đoàn Từ Đức----")],-1),y=JSON.parse('{"title":"Lắng nghe","description":"","frontmatter":{"title":"Lắng nghe","date":"2024-02-25T17:00:00.000Z","tags":["thienglieng"],"preview_image":"428629145_784613127030920_6798862236280608071_n.jpeg","prev":{"text":"Tĩnh tâm định hướng ơn gọi Dòng Tên Tháng Hai 2024","link":"/chiase/20240129001-tinh-tam-dinh-huong"},"next":{"text":"Thiên Chúa nơi việc học","link":"/chiase/20240227001-thien-chua-noi-viec-hoc"}},"headers":[],"relativePath":"chiase/20240226001-langnghe.md","filePath":"chiase/20240226001-langnghe.md"}'),p={name:"chiase/20240226001-langnghe.md"},b=Object.assign(p,{setup(v){return(k,_)=>{const h=g("Figure");return t(),i("div",null,[c(h,{src:a(e),caption:"Lắng nghe."},null,8,["src"]),m,l,o,s,u,r])}}});export{y as __pageData,b as default};
