import{D as g,o as t,c as i,I as c,m as o,k as n}from"./chunks/framework.AlgW7ztY.js";const a="/assets/434722452_807927624699470_1401660827694395698_n.ufYGV-Be.jpeg",e=n("p",null,"Kể từ khi thành lập, Jesuit Refugee Service (1980)- tổ chức cứu trợ người tị nạn Dòng Tên đã không ngừng chăm lo cho nhu cầu của người di dân, bao gồm người tị nạn, những người phải đổi chỗ ở trong nước, những người bị buôn bán,…Họ cần lương thực, cần mái nhà, cần tình yêu, cần tương quan, cần chân lý, cần ý nghĩa, cần lời hứu hẹn, cần niềm hy vọng, cần một tương lai trong đó họ nắm giữ trọn vẹn nhân phẩm của mình, và Dòng Tên đã đáp ứng những điều này.",-1),u=n("p",null,"Từ khi Dòng Tên lưu ý đến tình cảnh khốn cùng của những người di dân thì hiện tượng di dân do cưỡng bức dưới nhiều lý do đã gia tăng một cách đột ngột. Những làn sóng di cư ồ ạt này gây nên nỗi đau khổ ghế gớm cho hàng triệu con người. Hiện tượng di dân đã trở nên phức tạp hơn và tế nhị hơn. Những người này bị nhìn dưới con mắt thù địch bởi dư luận quần chúng ở những nơi tiếp nhận họ, chưa kể đến thực tế là những công cụ pháp chế để bệnh vực quyền lợi của họ tại đó lại mỏng giòn. Dòng Tên vẫn tiếp tục và canh tân sứ mạng của mình giữa người nghèo và với người nghèo.",-1),s=n("p",null,"Đáng tiếc là vẫn không thiếu những nguyên nhân mới xuất hiện, gây ra nghèo đói và tha hóa trong một thế giới mang nặng những bất bình đẳng sâu xa về kinh tế và môi trường: những tiến trình toàn háo hóa được thúc đẩy với lòng ích kỷ hơn là bởi tình liên đới; những xung đột vũ trang khốc liệt và vô lý,...",-1),l=n("p",null,"Vì thế, điều dễ hiểu là ai muốn trở nên bạn đường đích thực của Chúa Giê-su thì cũng chia sẻ thực sự tình yêu thương người nghèo với Ngài. Đối với tu sĩ Dòng Tên, lựa chọn người nghèo không phải là một ý thức hệ, mà phát sinh từ Tin Mừng.",-1),m=n("p",null,"Trong thế giới ngày nay, còn vô số tình huống bi thảm của bất công và nghèo khổ. Tu sĩ Dòng Tên vừa dấn thân để hiểu và chống lại các cơ cấu gây ra chúng, đồng thời cũng đi vào tận trái tim của con người để chống lại các gốc rễ thâm sâu của sự ác.",-1),r=n("p",null,"Nếu bạn khao khát tiếp nhận và phát huy một trong những mảng tông đồ lớn của Dòng Tên, đó là dấn thân một cách xứng đáng vào việc phục vụ người tị nạn, họ thường là những người nghèo nhất giữa những người nghèo; không chỉ cần những viện trợ khẩn cấp về vật chất, mà còn cần sự đồng hành gần gũi và sâu xa về mặt thiêng liêng, nhân bản và tâm lý, hãy đến với Dòng Tên, chúng ta tiếp tục viết nên những câu chuyện ý nghĩa giữa những con người nghèo, những người bạn đích thật của Chúa Giê-su.",-1),v=n("p",null,[n("em",null,"Lm. Giuse Ngô Văn Sơn, SJ")],-1),b=JSON.parse('{"title":"Tông đồ người nghèo","description":"","frontmatter":{"title":"Tông đồ người nghèo","date":"2024-04-04T17:00:00.000Z","tags":["tông đồ","ơn gọi"],"preview_image":"434722452_807927624699470_1401660827694395698_n.jpeg","prev":{"text":"Tĩnh tâm định hướng ơn gọi Dòng Tên Tháng Năm 2024 tại Hà Nội","link":"/chiase/20240402001-tinh-tam-dinh-huong"},"next":{"text":"Tông đồ tri thức","link":"/chiase/20240412001-tong-do-tri-thuc"}},"headers":[],"relativePath":"chiase/20240405001-tong-do-nguoi-ngheo.md","filePath":"chiase/20240405001-tong-do-nguoi-ngheo.md"}'),d={name:"chiase/20240405001-tong-do-nguoi-ngheo.md"},y=Object.assign(d,{setup(p){return(_,k)=>{const h=g("Figure");return t(),i("div",null,[c(h,{src:o(a),caption:"Tông đồ người nghèo."},null,8,["src"]),e,u,s,l,m,r,v])}}});export{b as __pageData,y as default};
