import{D as g,o as a,c as o,I as i,m as t,k as n,a as c,R as s}from"./chunks/framework.iYlBf-hB.js";const u="/assets/founding.NXnBTqix.jpeg",l="/assets/lt.DOZWT-qU.jpeg",e="/assets/ht.rDDwoeLI.jpeg",r="/assets/sinhvien.0gYq5390.jpeg",m=n("h2",{id:"lich-su",tabindex:"-1"},[c("Lịch sử "),n("a",{class:"header-anchor",href:"#lich-su","aria-label":'Permalink to "Lịch sử"'},"​")],-1),v=s("",8),p=n("p",null,"Linh đạo Dòng Tên được đặt trên nền tảng linh đạo I-nhã và Hiến Luật Dòng. Dựa trên những kinh nghiệm thiêng liêng được đúc kết trong tập Linh Thao, sách Tự Thuật và Nhật Ký Thiêng Liêng, thánh I-nhã đã chia sẻ với Giáo Hội một linh đạo lấy Đức Giê-su Ki-tô làm trung tâm cho cuộc sống, với lòng ao ước được hiểu biết Chúa hơn, yêu mến Chúa hơn và bước theo sát Chúa hơn. Cùng bước theo Chúa Giê-su vác thánh giá để thi hành thánh ý của Chúa Cha, thánh I-nhã đã chia sẻ với mọi người cách thức thực hành việc phân định thiêng liêng để khám phá lời mời gọi yêu thương của Thiên Chúa dành cho mỗi người và quảng đại đáp lại lời mời gọi ấy trong từng giây phút của cuộc sống thường ngày.",-1),d=n("p",null,"Lối sống và cung cách hành xử của các Giê-su hữu thấm nhuần linh đạo của thánh I-nhã qua những kinh nghiệm thiêng liêng của Linh Thao, và được hướng dẫn bởi Hiến Luật của Dòng. Tình yêu cá vị đối với Thiên Chúa và cụ thể đối với Đức Giê-su Ki-tô là nền tảng thúc đẩy từng Giê-su hữu khám phá sự hiện diện và lời mời gọi của Thiên Chúa trong mọi người và trong mọi hoàn cảnh, thúc đẩy từng Giê-su hữu cùng với Đức Ki-tô lao tác để làm tất cả mọi sự cho Vinh Danh Chúa Hơn trong phân định thiêng liêng, nhằm giúp đỡ các linh hồn qua các sứ vụ khác nhau.",-1),T=n("p",null,"Chính lòng khao khát muốn nên giống Đức Giê-su vác thập giá trong mọi sự và muốn cùng với Đức Giê-su Ki-tô lao tác đã làm cho các Giê-su hữu trở nên những người bạn trong Đức Ki-tô, và nối kết họ thành một thân thể tông đồ với đức vâng phục làm nền tảng – vâng phục Đức Giáo Hoàng, Bề Trên Tổng Quyền và các Bề Trên hợp pháp – với cùng một cảm thức với Giáo Hội để luôn tìm điều hơn (magis) cho Vinh Danh Chúa, để phục vụ Giáo Hội và mang ích lợi cho con người.",-1),_=n("h2",{id:"su-mang",tabindex:"-1"},[c("Sứ mạng "),n("a",{class:"header-anchor",href:"#su-mang","aria-label":'Permalink to "Sứ mạng"'},"​")],-1),b=n("p",null,"Phục vụ đức tin nối kết với thăng tiến công bình của Nước Thiên Chúa trong đối thoại với các nền văn hoá và các tôn giáo khác. Trong bối cảnh của một thế giới đang bị ảnh hưởng bởi trào lưu toàn cầu hóa, dẫn tới gia tăng những mâu thuẫn và xung đột, sứ mạng này được thực hiện ngang qua nhiều sứ vụ như giảng Linh Thao, tông đồ xã hội, mục vụ giới trẻ, suy tư và giảng dạy thần học, truyền thông… nhằm thiết lập những nhịp cầu hòa giải giữa con người với Thiên Chúa, giữa con người với nhau và với môi trường thiên nhiên.",-1),k=n("h2",{id:"cac-hoat-đong-tai-viet-nam",tabindex:"-1"},[c("Các hoạt động tại Việt Nam "),n("a",{class:"header-anchor",href:"#cac-hoat-đong-tai-viet-nam","aria-label":'Permalink to "Các hoạt động tại Việt Nam"'},"​")],-1),y=n("ul",null,[n("li",null,"Giúp Linh Thao"),n("li",null,"Suy tư và giảng dạy triết học & thần học"),n("li",null,"Mục vụ giáo xứ"),n("li",null,"Mục vụ sinh viên và di dân"),n("li",null,"Truyền giáo"),n("li",null,"Tông đồ xã hội"),n("li",null,"Linh hướng chủng viện"),n("li",null,"Truyền thông xã hội"),n("li",null,"Các sứ mạng quốc tế")],-1),N=JSON.parse('{"title":"Dòng Tên","description":"","frontmatter":{"title":"Dòng Tên"},"headers":[],"relativePath":"dongten.md","filePath":"dongten.md"}'),C={name:"dongten.md"},x=Object.assign(C,{setup(G){return(q,L)=>{const h=g("Figure");return a(),o("div",null,[m,i(h,{src:t(u),caption:"I-nhã và nhóm bạn đầu tiên tuyên khấn tại Montmatre."},null,8,["src"]),v,i(h,{src:t(l),caption:"Linh thao."},null,8,["src"]),p,d,T,_,b,k,i(h,{src:t(e),caption:"Giúp linh thao Chủng Viện."},null,8,["src"]),i(h,{src:t(r),caption:"Tông đồ sinh viên."},null,8,["src"]),y])}}});export{N as __pageData,x as default};
