import{D as g,o as a,c as o,I as i,m as t,k as n,a as c,R as s}from"./chunks/framework.AlgW7ztY.js";const u="/assets/founding.NXnBTqix.jpeg",l="/assets/lt.DOZWT-qU.jpeg",e="/assets/ht.rDDwoeLI.jpeg",r="/assets/sinhvien.0gYq5390.jpeg",m=n("h2",{id:"lich-su",tabindex:"-1"},[c("Lịch sử "),n("a",{class:"header-anchor",href:"#lich-su","aria-label":'Permalink to "Lịch sử"'},"​")],-1),v=s('<p>Thiên Chúa đã ban cho Giáo Hội một đặc sủng ngang qua một con người – thánh I-nhã xứ Loyola (Ignace de Loyola, 1491-1556) – và một nhóm bạn cùng chí hướng. Sau khi được ơn hoán cải ở tuổi 30, thánh I-nhã đã quyết tâm phục vụ Đức Ki-tô. Ngài đã có nhiều kinh nghiệm thiêng liêng đặc biệt mà sau này được đúc kết thành tập sách nhỏ Linh Thao.</p><p>Trong thời gian học ở đại học Pa-ri, I-nhã đã quy tụ được một nhóm bạn cùng chí hướng qua việc hướng dẫn Linh Thao cho từng người. Trong số đó, có thánh Phan-xi-cô Xa-vi-ê và thánh Phê-rô Favre. Ngày 15.8.1534, nhóm bạn của I-nhã đã cam kết sống chung với nhau trong một cộng đoàn, khấn khó nghèo, làm việc tông đồ và đi hành hương Đất Thánh. Lúc đó, nhóm không có tên, không có luật, không ai có quyền trên ai. Họ chỉ liên kết với nhau vì chia sẻ cùng một lý tưởng tông đồ và sống tình bạn thiêng liêng. Năm 1537, cả nhóm đã tụ họp về Venezia để chuẩn bị đi hành hương Thánh Địa. Trong thời gian chờ đợi, họ chia ra từng nhóm nhỏ đi phục vụ, dưới danh nghĩa là nhóm bạn đồng hành của Chúa Giê-su.</p><p>Do không thể đi Thánh Địa vì có chiến tranh, cả nhóm quyết định đi Roma vào cuối năm 1537 để đặt mình dưới sự hướng dẫn trực tiếp của vị thủ lãnh Giáo Hội hầu được sai đi bất cứ nơi đâu trên thế giới để phục vụ các linh hồn. Mùa Chay năm 1539, họ gặp nhau nhiều lần để cùng phân định hướng đi cho tương lai. Cuối cùng, họ quyết định liên kết với nhau qua lời khấn vâng phục một người trong nhóm và trở thành một dòng tu để hỗ trợ nhau trong sứ mạng phục vụ Đức Ki-tô.</p><p>Ngày 27.9.1540, ĐGH Phao-lô III chính thức công bố trọng sắc Regimini Militantis Ecclesiae phê chuẩn Đoàn Giê-su thành một dòng tu giáo sĩ trong Hội Thánh. Từ đó Đoàn Giê-su trở thành Dòng Chúa Giê-su (The Society of Jesus – ở Việt Nam quen gọi là Dòng Tên, vì muốn kính Tên cực trọng Giê-su). Mười thành viên trong dòng tu mới lập đều nhất trí bầu cha I-nhã làm Bề trên Tổng Quyền tiên khởi (1540-1556).</p><p>Tháng 01 năm 1615, ba Giê-su hữu đã đặt chân đến Cửa Hàn, Đà Nẵng thuộc Đàng Trong, mở đường cho hành trình loan báo Tin Mừng trên Đất Việt.</p><h2 id="đac-sung" tabindex="-1">Đặc sủng <a class="header-anchor" href="#đac-sung" aria-label="Permalink to &quot;Đặc sủng&quot;">​</a></h2><p>Bước theo Chúa Giê-su vác thánh giá, trung thành với Giáo Hội và sẵn sàng để được vị đại diện của Người là Đức Giáo Hoàng sai đi đến bất cứ nơi nào, làm bất cứ việc gì theo các giá trị Tin Mừng nhằm Tôn Vinh Thiên Chúa Hơn và giúp ích cho con người hôm nay hơn, theo tinh thần chiêm niệm trong hoạt động.</p><h2 id="linh-đao" tabindex="-1">Linh đạo <a class="header-anchor" href="#linh-đao" aria-label="Permalink to &quot;Linh đạo&quot;">​</a></h2>',8),p=n("p",null,"Linh đạo Dòng Tên được đặt trên nền tảng linh đạo I-nhã và Hiến Luật Dòng. Dựa trên những kinh nghiệm thiêng liêng được đúc kết trong tập Linh Thao, sách Tự Thuật và Nhật Ký Thiêng Liêng, thánh I-nhã đã chia sẻ với Giáo Hội một linh đạo lấy Đức Giê-su Ki-tô làm trung tâm cho cuộc sống, với lòng ao ước được hiểu biết Chúa hơn, yêu mến Chúa hơn và bước theo sát Chúa hơn. Cùng bước theo Chúa Giê-su vác thánh giá để thi hành thánh ý của Chúa Cha, thánh I-nhã đã chia sẻ với mọi người cách thức thực hành việc phân định thiêng liêng để khám phá lời mời gọi yêu thương của Thiên Chúa dành cho mỗi người và quảng đại đáp lại lời mời gọi ấy trong từng giây phút của cuộc sống thường ngày.",-1),d=n("p",null,"Lối sống và cung cách hành xử của các Giê-su hữu thấm nhuần linh đạo của thánh I-nhã qua những kinh nghiệm thiêng liêng của Linh Thao, và được hướng dẫn bởi Hiến Luật của Dòng. Tình yêu cá vị đối với Thiên Chúa và cụ thể đối với Đức Giê-su Ki-tô là nền tảng thúc đẩy từng Giê-su hữu khám phá sự hiện diện và lời mời gọi của Thiên Chúa trong mọi người và trong mọi hoàn cảnh, thúc đẩy từng Giê-su hữu cùng với Đức Ki-tô lao tác để làm tất cả mọi sự cho Vinh Danh Chúa Hơn trong phân định thiêng liêng, nhằm giúp đỡ các linh hồn qua các sứ vụ khác nhau.",-1),T=n("p",null,"Chính lòng khao khát muốn nên giống Đức Giê-su vác thập giá trong mọi sự và muốn cùng với Đức Giê-su Ki-tô lao tác đã làm cho các Giê-su hữu trở nên những người bạn trong Đức Ki-tô, và nối kết họ thành một thân thể tông đồ với đức vâng phục làm nền tảng – vâng phục Đức Giáo Hoàng, Bề Trên Tổng Quyền và các Bề Trên hợp pháp – với cùng một cảm thức với Giáo Hội để luôn tìm điều hơn (magis) cho Vinh Danh Chúa, để phục vụ Giáo Hội và mang ích lợi cho con người.",-1),_=n("h2",{id:"su-mang",tabindex:"-1"},[c("Sứ mạng "),n("a",{class:"header-anchor",href:"#su-mang","aria-label":'Permalink to "Sứ mạng"'},"​")],-1),b=n("p",null,"Phục vụ đức tin nối kết với thăng tiến công bình của Nước Thiên Chúa trong đối thoại với các nền văn hoá và các tôn giáo khác. Trong bối cảnh của một thế giới đang bị ảnh hưởng bởi trào lưu toàn cầu hóa, dẫn tới gia tăng những mâu thuẫn và xung đột, sứ mạng này được thực hiện ngang qua nhiều sứ vụ như giảng Linh Thao, tông đồ xã hội, mục vụ giới trẻ, suy tư và giảng dạy thần học, truyền thông… nhằm thiết lập những nhịp cầu hòa giải giữa con người với Thiên Chúa, giữa con người với nhau và với môi trường thiên nhiên.",-1),k=n("h2",{id:"cac-hoat-đong-tai-viet-nam",tabindex:"-1"},[c("Các hoạt động tại Việt Nam "),n("a",{class:"header-anchor",href:"#cac-hoat-đong-tai-viet-nam","aria-label":'Permalink to "Các hoạt động tại Việt Nam"'},"​")],-1),y=n("ul",null,[n("li",null,"Giúp Linh Thao"),n("li",null,"Suy tư và giảng dạy triết học & thần học"),n("li",null,"Mục vụ giáo xứ"),n("li",null,"Mục vụ sinh viên và di dân"),n("li",null,"Truyền giáo"),n("li",null,"Tông đồ xã hội"),n("li",null,"Linh hướng chủng viện"),n("li",null,"Truyền thông xã hội"),n("li",null,"Các sứ mạng quốc tế")],-1),N=JSON.parse('{"title":"Dòng Tên","description":"","frontmatter":{"title":"Dòng Tên"},"headers":[],"relativePath":"dongten.md","filePath":"dongten.md"}'),C={name:"dongten.md"},x=Object.assign(C,{setup(G){return(q,L)=>{const h=g("Figure");return a(),o("div",null,[m,i(h,{src:t(u),caption:"I-nhã và nhóm bạn đầu tiên tuyên khấn tại Montmatre."},null,8,["src"]),v,i(h,{src:t(l),caption:"Linh thao."},null,8,["src"]),p,d,T,_,b,k,i(h,{src:t(e),caption:"Giúp linh thao Chủng Viện."},null,8,["src"]),i(h,{src:t(r),caption:"Tông đồ sinh viên."},null,8,["src"]),y])}}});export{N as __pageData,x as default};
